import React from "react";
import ReactDOM from "react-dom";
import initial_data from "./initialData";
import "./index.css";
import reportWebVitals from "./reportWebVitals";
import Column_cmp from "./Cmps/Column_cmp";
import { DragDropContext } from "react-beautiful-dnd";

class App extends React.Component {
  state = initial_data;

  onDragEnd = (result) => {
    const { destination, source, draggableId } = result;
    console.log('destination',destination);
    console.log('source',source);
    console.log('draggableId',draggableId );

    if (!destination) {
      return;
    }

    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }

    const column = this.state.columns[source.draggableId];
    console.log(this.state.columns );

    const newTaskId = Array.from(column.taskIds);
    newTaskId.splice(source.index, 1);
    newTaskId.splice(destination.index, 0, draggableId);

    const newColumn = {
      ...column,
      taskId: newTaskId,
    };

    const newState = {
      ...this.state,
      columns: {
        ...this.state.columns,
        [newColumn.id]: newColumn,
      },
    };
    this.setState(newState);
  };

  render() {
    return (
      <DragDropContext onDragEnd={this.onDragEnd}>
        {this.state.columnOder.map((column_id) => {
          const column = this.state.columns[column_id],
            tasks = column.taskIds.map((task_id) => this.state.tasks[task_id]);
          return <Column_cmp key={column.id} column={column} tasks={tasks} />;
        })}
      </DragDropContext>
    );
  }
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);

reportWebVitals();
