import React from "react";
import ReactDOM from "react-dom";
import initial_data from "./initialData";
import "./index.css";
import reportWebVitals from "./reportWebVitals";
import Column_cmp from "./Cmps/Column_cmp";
import { DragDropContext } from "react-beautiful-dnd";

class App extends React.Component {
  state = initial_data;

  onDragEnd = (result) => {
    const { destination, source, draggablId } = result;
    if (!destination) {
      return;
    }
    if (
      destination.draggablId === source.draggablId &&
      destination.index === source.index
    ) {
      return;
    }
    const column = this.state.columns[source.draggablId]
    const newTaskId = Array.from(column.taskIds)

  };

  render() {
    return (
      <DragDropContext onDragEnd={this.onDragEnd}>
        {this.state.columnOder.map((column_id) => {
          const column = this.state.columns[column_id],
            tasks = column.taskIds.map((task_id) => this.state.tasks[task_id]);
          return <Column_cmp key={column.id} column={column} tasks={tasks} />;
        })}
      </DragDropContext>
    );
  }
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);

reportWebVitals();
