import React from "react";
import ReactDOM from "react-dom";
import initial_data from "./initialData";
import "./index.css";
import reportWebVitals from "./reportWebVitals";
import Column_cmp from "../Column_cmp";
// import Column_cmp from "../Cmps/Column_cmp";
import { DragDropContext } from "react-beautiful-dnd";
import { useState, useEffect } from "react";
import ToolBar from "../ToolBar";

export default function App() {
  const [state, setState] = useState(initial_data);
  
  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    if (!destination) {
      return;
    }

    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }

    const start = state.columns[source.droppableId];
    const finish = state.columns[destination.droppableId];

    if (start === finish) {
      const newTaskId = Array.from(start.taskIds);

      newTaskId.splice(source.index, 1);
      newTaskId.splice(destination.index, 0, draggableId);

      const newColumn = {
        ...start,
        taskIds: newTaskId,
      };

      const newState = {
        ...state,
        columns: {
          ...state.columns,
          [newColumn.id]: newColumn,
        },
      };
      setState(newState);
      return;
    }

    const startTaskIds = Array.from(start.taskIds);
    startTaskIds.splice(source.index, 1);
    const newStart = {
      ...start,
      taskIds: startTaskIds,
    };

    const finshTaskIds = Array.from(finish.taskIds);
    finshTaskIds.splice(destination.index, 0, draggableId);
    const newFinsh = {
      ...finish,
      taskIds: finshTaskIds,
    };

    const newState = {
      ...state,
      columns: {
        ...state.columns,
        [newStart.id]: newStart,
        [newFinsh.id]: newFinsh,
      },
    };
    setState(newState);
    return;
  };

  return ( <DragDropContext onDragEnd={onDragEnd}>
    <ToolBar/>
    {state.columnOder.map((column_id) => {
      const column = state.columns[column_id],
        tasks = column.taskIds.map((task_id) => state.tasks[task_id]);
      return <Column_cmp key={column.id} column={column} tasks={tasks} />;
    })}
  </DragDropContext>)
}
