import React from "react";
import initial_data from "../../initialData";
import Column_cmp from "./Column_cmp";
import { DragDropContext } from "react-beautiful-dnd";
import { useState, useEffect } from "react";
import ToolBar from "./ToolBar";
import Requests from "../../Functions/Requests";
import ColumnCmp from "./ColumnCmp";
import CreateTask from "./CreateTask";

export default function ToDoListCmp() {
  const [tasks_list, setTasksList] = useState();
  const [openCreateTask, setOpenCreateTask] = useState(false);

  let data_user = JSON.parse(localStorage.getItem("data_user"));
  const user_id = data_user["user_id"];
  const user_name = data_user["user_name"];

  useEffect(() => {
    async function tasks_list_useEffect() {
      Requests.get_task_list(user_id).then((res) => {
        setTasksList(res.data.tasks_list);
      });
    }
    tasks_list_useEffect();
  }, []);

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;
    // console.log("result", result);

    if (!destination) {
      return;
    }

    if (
      destination.droppableId == source.droppableId &&
      destination.index == source.index
    ) {
      return;
    }

    const start = source.droppableId;
    const finish = destination.droppableId;
    // console.log("start", source.droppableId, "finish", destination.droppableId);

    if (start === finish) {
      const tasks_status_list = tasks_list.filter(
        (task) => task.status === start
      );

      console.log("tasks_status_list", tasks_status_list);
      const element = tasks_status_list[source.index];
      tasks_status_list.splice(source.index, 1);
      tasks_status_list.splice(destination.index, 0, element);

      console.log('eleman',element);

      tasks_status_list.splice(source.index, 1);
      tasks_status_list.splice(destination.index, 0, element);

      console.log(tasks_status_list);

      // console.log(tasks_status_list);
      // new_tasks_list.splice(destination.index, 0, draggableId);
      // setTasksList(new_tasks_list_2.concat(new_tasks_list));

      // console.log("result", result);
      // console.log("e", destination.index);
      // console.log("s", source.index);

      // const new_tasks_list = Array.from(tasks_status_list);
      // console.log("old task list", new_tasks_list);
      // console.log('new tasks list',newTaskId);

      // newTaskId.splice(source.index, 1);
      // newTaskId.splice(destination.index, 0, draggableId);
      // console.log(newTaskId);

      // source

      // let new_tasks_list = tasks_list.filter((task) => task.status === start);

      // let new_tasks_list_2 = tasks_list.filter((task) => task.status != start);

      // console.log(new_tasks_list_2);

      // const newTaskId = Array.from(start.taskIds);
      // console.log(start);

      // newTaskId.splice(source.index, 1);
      // newTaskId.splice(destination.index, 0, draggableId);

      // const newColumn = {
      //   ...start,
      //   taskIds: newTaskId,
    }

    //   const newState = {
    //     ...state,
    //     columns: {
    //       ...state.columns,
    //       [newColumn.id]: newColumn,
    //     },
    //   };
    //   setState(newState);
    //   return;
    // }

    // const startTaskIds = Array.from(start.taskIds);
    // startTaskIds.splice(source.index, 1);
    // const newStart = {
    //   ...start,
    //   taskIds: startTaskIds,
    // };

    // const finshTaskIds = Array.from(finish.taskIds);
    // finshTaskIds.splice(destination.index, 0, draggableId);
    // const newFinsh = {
    //   ...finish,
    //   taskIds: finshTaskIds,
    // };

    // const newState = {
    //   ...state,
    //   columns: {
    //     ...state.columns,
    //     [newStart.id]: newStart,
    //     [newFinsh.id]: newFinsh,
    //   },
    // };
    // setState(newState);
    // return;
  };

  let data_cmp = [
    { title: "TODO", status: "todo" },
    {
      title: "IN PROGERESS",
      status: "in_pragress",
    },
    { title: "DONE", status: "done" },
  ];

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <ToolBar user_name={user_name} />
      {openCreateTask ? (
        <CreateTask
          state_function={{
            setTasksList: setTasksList,
            setOpenCreateTask: setOpenCreateTask,
          }}
          user_id={user_id}
        />
      ) : (
        ""
      )}
      <button
        onClick={() => {
          setOpenCreateTask(true);
        }}
      >
        add
      </button>

      <div className="pro">
        {data_cmp.map((data_column, index) => (
          <ColumnCmp
            delete={async (task_id) => {
              Requests.delete_task(task_id);
              let new_tasks_list = tasks_list.filter(
                (task) => task.task_id !== task_id
              );
              setTasksList(new_tasks_list);
            }}
            move={(task_id, status_task) => {
              Requests.post_value("/move", {
                task_id: task_id,
                status_task: status_task,
              }).then((res) => {
                const task_found = tasks_list.find(
                  (task) => task.task_id === task_id
                );
                task_found.status = res.data.move_to_status;
                setTasksList((task_found) => [...task_found]);
              });
            }}
            class_name={data_column.status}
            title={data_column.title}
            key={index}
            number={index}
            tasks_list={
              tasks_list
                ? tasks_list.filter(
                    (task) => task.status === data_column.status
                  )
                : ""
            }
          />
        ))}
      </div>
    </DragDropContext>
  );
}
