import React, { Component } from "react";
import { Draggable } from "react-beautiful-dnd";

export default class Task_cmp extends Component {
  change_color_of_item = (isDragging, id) => {
    if (isDragging) {
      document.getElementById(id).classList.add("isdragging");
    } else {
      if (document.querySelector(`#${id}`)) {
        document.getElementById(id).classList.remove("isdragging");
      }
    }
  }

  render() {
    return (
      <Draggable draggableId={this.props.task.id} index={this.props.index}>
        {(provided, snapshot) => (
          <div
            className="task"
            id={this.props.task.id}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
            ref={provided.innerRef}
            isdragging={this.change_color_of_item(
              snapshot.isDragging,
              this.props.task.id
            )}
          >
            {this.props.task.content}
          </div>
        )}
      </Draggable>
    );
  }
}
