import React, { Component } from "react";
import { Draggable } from "react-beautiful-dnd";

export default class Task_cmp extends Component {
  render() {
    return (
      <Draggable draggableId={this.props.task.id} index={this.props.index}>
        {(provided,snapshot) => (
          <div
            className="task"
            id={this.props.task.id}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
            ref={provided.innerRef}
            isdragging={snapshot.isDragging?document.getElementById(this.props.task.id).classList.add('isdragging'):document.getElementById(this.props.task.id).classList.remove('isdragging')}
            
          >
            {this.props.task.content}
          </div>
        )}
      </Draggable>
    );
  }
}
