// import React from 'react'

export default function Task_cmp(props) {
  change_color_of_item = (isDragging, id,draggingOver) => {
    console.log(draggingOver);
    if (isDragging) {
      document.getElementById(id).classList.add("isdragging");
    } else {
      if (document.querySelector(`#${id}`)) {
        document.getElementById(id).classList.remove("isdragging");
      }
    }
  }


  return (
    <Draggable draggableId={this.props.task.id} index={this.props.index}>
        {(provided, snapshot) => (
          <div
            className="task"
            id={this.props.task.id}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
            ref={provided.innerRef}
            
            isdragging={change_color_of_item(
              snapshot.isDragging,
              this.props.task.id,
              snapshot.draggingOver
            )}
          >
            {this.props.task.content}
          </div>
        )}
      </Draggable>
  )
}
