import React from "react";

export default function TaskCmp(props) {
  const status_task = props.task.status;
  const task_id = props.task.task_id;
  return (
    <li
      className={daragging ? get_style(status_task, task_id) : "task"}
      onDragEnter={(e) => {
        drag_enter(e, { task_id, status_task });
      }}
      draggable
      onDragStart={(e) => {
        drag_start(e, { task_id, status_task });
      }}
    >
      <div>
        <div
          onClick={() => {
            props.delete(task_id);
          }}
        >
          delete
        </div>
        <span>{props.task.title}</span>
        <span>{props.task.descriptiio}</span>
        <span>{props.task.date}</span>
        <div onClick={() => props.move(task_id, "in_pragress")}>done</div>
        <div
          onClick={() => {
            props.move(task_id, status_task);
          }}
        >
          move
        </div>
      </div>
    </li>
  );
}
