import React, { Component } from "react";
import { Draggable } from "react-beautiful-dnd";

export default class Task_cmp extends Component {
  change_color_of_item=(isDragging,id)=>{
    if (isDragging) {
      document.getElementById(id).classList.add('isdragging')
    }else{
      document.querySelector(`#${id}`)
    }
  }

  render() {
    return (
      <Draggable draggableId={this.props.task.id} index={this.props.index}>
        {(provided,snapshot) => (
          <div
            className="task"
            id={this.props.task.id}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
            ref={provided.innerRef}
            isdragging={snapshot.isDragging?document.getElementById(this.props.task.id).classList.add('isdragging'):console.log(snapshot.draggingOver)}
            
          >
            {this.props.task.content}
          </div>
        )}
      </Draggable>
    );
  }
}
