import React from "react";
import { Draggable } from "react-beautiful-dnd";

export default function TaskCmp(props) {
  const status_task = props.task.status;
  const task_id = props.task.task_id;

  return (
    <Draggable draggableId={`${task_id}`} index={props.number}>
      {(provided) => (
        <li
          className="task_li"
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          ref={provided.innerRef}
        >
          <div
            onClick={() => {
              props.delete(task_id);
            }}
          >
            delete
          </div>
          <span>{props.task.title}</span>
          <span>{props.task.descriptiio}</span>
          <span>{props.task.date}</span>
          <div
            onClick={() => {
              props.move(task_id, "done");
              // props.move(task_id, "in_pragress");
            }}
          >
            done
          </div>
          <div
            onClick={() => {
              let value_status = "";
              switch (status_task) {
                case "todo":
                  value_status = "in_pragress";
                  break;
                case "in_pragress":
                  value_status = "done";
                  break;
                case "done":
                  value_status = "todo";
              }
              props.move(task_id, value_status);
            }}
          >
            move
          </div>
        </li>
      )}
    </Draggable>
  );
}
