import React from "react";
import { Draggable } from "react-beautiful-dnd";

export default function TaskCmp(props) {
  const status_task = props.task.status;
  const task_id = props.task.task_id;


  const change_color_of_item = (isDragging, id, draggingOver) => {
    console.log(document.getElementById(id));
    if (isDragging) {
      document.getElementById(id).classList.add("isdragging");
    } else {
      if (document.querySelector(`#${id}`)) {
        document.getElementById(id).classList.remove("isdragging");
      }
    }
  };
  
 
 

  return (
    
    <Draggable draggableId={`${props.number1}`} index={props.number}>
    

      {(provided,snapshot) => (
        <li
          className="task_li"
          id={`${task_id}`}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          ref={provided.innerRef}
          isdragging={change_color_of_item(
            snapshot.isDragging,
            task_id,
            snapshot.draggingOver
          )}
        >
          <div
            onClick={() => {
              props.delete(task_id);
            }}
          >
            delete
          </div>
          <span>{props.task.title}</span>
          <span>{props.task.descriptiio}</span>
          <span>{props.task.date}</span>
          <div
            onClick={() => {
              props.move(task_id, "done");
            }}
          >
            done
          </div>
          <div
            onClick={() => {
              let value_status = "";
              switch (status_task) {
                case "todo":
                  value_status = "in_pragress";
                  break;
                case "in_pragress":
                  value_status = "done";
                  break;
                case "done":
                  value_status = "todo";
              }
              props.move(task_id, value_status);
            }}
          >
            move
          </div>
        </li>
      )}
    </Draggable>
  );
}
