import React from "react";
import { Draggable } from "react-beautiful-dnd";

export default function TaskCmp(props) {
  const status_task = props.task.status;
  const task_id = props.task.task_id;
  return (
    <Draggable draggableId={task_id} index={props.number}>
      {(provided) => (
        <li
          className="task"
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          ref={provided.innerRef}
        >
          <div>
            <div
              onClick={() => {
                props.delete(task_id);
              }}
            >
              delete
            </div>
            <span>{props.task.title}</span>
            <span>{props.task.descriptiio}</span>
            <span>{props.task.date}</span>
            <div
            onClick={() => props.move(task_id, "in_pragress")}
            >
              done
            </div>
            <div
              onClick={() => {
                props.move(task_id, status_task);
              }}
            >
              move
            </div>
          </div>
        </li>
      )}
