import React from "react";
import Requests from "../../Functions/Requests";

export default function RegisterWindowCmp(props) {
  const onHandleSubmit = (event) => {
    event.preventDefault();
    let value_input = {
      password: document.querySelector('[name="password"]').value,
      username: document.querySelector('[name="username"]').value,
    };
    Requests.post_value(props.request, value_input).then((res) => {
      if (res.data.user_id) {
        localStorage.setItem(
          "data_user",
          JSON.stringify({
            user_id: res.data.user_id,
            user_name: res.data.user_name,
          })
        );
        window.location.href = "/#/ToDoList";
      } else {
        console.log("operation failed, try again");
      }
    });
  };

  return (
    <section>
      <h2>{props.title}</h2>
      <form name="form_window" action={props.request} onSubmit={onHandleSubmit}>
        <input type="text" placeholder="User name" name="username" />
        <input type="text" placeholder="Password" name="password" />
        <button>{props.button_name}</button>
      </form>
    </section>
  );
}
