import React from "react";
import ReactDOM from "react-dom";
import initial_data from "./initialData";
import "./index.css";
import reportWebVitals from "./reportWebVitals";
import Column_cmp from "./Cmps/Column_cmp";
import { DragDropContext } from "react-beautiful-dnd";

class App extends React.Component {
  state = initial_data;

  onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    if (!destination) {
      return;
    }

    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }

    const start = this.state.columns[source.droppableId];
    const finish = this.state.columns[destination.droppableId];

    if(start===finish){
      const newTaskId = Array.from(start.taskIds);

    newTaskId.splice(source.index, 1);
    newTaskId.splice(destination.index, 0, draggableId);

    const newColumn = {
      ...start,
      taskIds: newTaskId,
    };

    const newState = {
      ...this.state,
      columns: {
        ...this.state.columns,
        [newColumn.id]: newColumn,
      },
    };
    this.setState(newState);
    return}

    const startTaskIds = Array.from(start.taskIds)
    startTaskIds.splice(source.index,1)
    const newStart ={
      ...start,
      taskIds:startTaskIds
    }

    const finshTaskIds = Array.from(finish.taskIds)
    finshTaskIds.splice(destination.index,0,draggableId);
    const newState={
      ...this.state,
      columns:{
        ...this.state.column
      }
    }
  };

  render() {
    return (
      <DragDropContext onDragEnd={this.onDragEnd}>
        {this.state.columnOder.map((column_id) => {
          const column = this.state.columns[column_id],
            tasks = column.taskIds.map((task_id) => this.state.tasks[task_id]);
          return <Column_cmp key={column.id} column={column} tasks={tasks} />;
        })}
      </DragDropContext>
    );
  }
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);

