// import React from 'react'

export default function Task_cmp(props) {
  const change_color_of_item = (isDragging, id, draggingOver) => {
    console.log(draggingOver);
    if (isDragging) {
      document.getElementById(id).classList.add("isdragging");
    } else {
      if (document.querySelector(`#${id}`)) {
        document.getElementById(id).classList.remove("isdragging");
      }
    }
  };

  return (
    <Draggable draggableId={props.task.id} index={props.index}>
      {(provided, snapshot) => (
        <div
          className="task"
          id={props.task.id}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          ref={provided.innerRef}
          isdragging={change_color_of_item(
            snapshot.isDragging,
            props.task.id,
            snapshot.draggingOver
          )}
        >
          {props.task.content}
        </div>
      )}
    </Draggable>
  );
}
